import { useState } from "react";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";

const SignInPage = () => {
  const [role, setRole] = useState(null);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const roles = ["landowner", "skilled-labour", "entrepreneur", "supplier"];

  const handleSignIn = (e) => {
    e.preventDefault();
    console.log("Signing in as", role, "with email:", email);
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-100">
      <Card className="p-6 w-96">
        <CardContent>
          {!role ? (
            <div className="space-y-4">
              <h2 className="text-xl font-bold text-center">Select Your Role</h2>
              {roles.map((r) => (
                <Button
                  key={r}
                  className="w-full"
                  onClick={() => setRole(r)}
                >
                  {r.charAt(0).toUpperCase() + r.slice(1)}
                </Button>
              ))}
            </div>
          ) : (
            <div className="space-y-4">
              <h2 className="text-xl font-bold text-center">Sign In as {role}</h2>
              <form onSubmit={handleSignIn} className="space-y-4">
                <input
                  type="email"
                  placeholder="Email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  className="w-full p-2 border rounded"
                  required
                />
                <input
                  type="password"
                  placeholder="Password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  className="w-full p-2 border rounded"
                  required
                />
                <Button type="submit" className="w-full">Sign In</Button>
              </form>
              <Button
                className="w-full mt-4"
                variant="outline"
                onClick={() => setRole(null)}
              >
                Change Role
              </Button>
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
};

export default SignInPage;
