import React from "react";
import { useParams } from "react-router-dom";
import { entrepreneurs, landowners, skilledLabor, suppliers } from "./data";

const ProfileDetail = () => {
  const { role, id } = useParams();

  const dataMap = {
    "skilled-labor": skilledLabor,
    entrepreneur: entrepreneurs,
    landowner: landowners,
    supplier: suppliers,
  };

  const userDetails = dataMap[role]?.find((user) => user.id === parseInt(id));

  if (!userDetails) return <p>Profile not found.</p>;

  return (
    <div className="profile-detail-container">
      <h2>{userDetails.name}</h2>
      {role === "entrepreneur" && (
        <>
          <p><b>Business:</b> {userDetails.business}</p>
          <p><b>Contact No.:</b> {userDetails.contact}</p>
          <p><b>Location:</b> {userDetails.location}</p>
        </>
      )}
      {role === "skilled-labor" && (
        <>
          <p><b>Skill:</b> {userDetails.skill}</p>
          <p><b>Experience:</b> {userDetails.experience} years</p>
          <p><b>Expected Salary:</b> ${userDetails.expectedSalary}</p>
        </>
      )}
      {role === "landowner" && (
        <>
          <p><b>Land Size:</b> {userDetails.landSize}</p>
          <p><b>Expected Payment:</b> ${userDetails.expectedPayment}</p>
          <p><b>Location:</b> {userDetails.location}</p>
          <p><b>For:</b> {userDetails.rentOrSell}</p>
        </>
      )}
      {role === "supplier" && (
        <>
          <p><b>Products Supplied:</b> {userDetails.products}</p>
          <p><b>Minimum Order Quantity:</b> {userDetails.minOrder}</p>
          <p><b>Delivery Areas:</b> {userDetails.deliveryAreas}</p>
        </>
      )}
    </div>
  );
};

export default ProfileDetail;
